import numpy as npfrom gym_exchange import Configdef vwap_price(pairs):    ''' pairs format    price:    array([[ 1. ,  1. ,  1. ,  1.1,  0.9],    quantity:        [ 2. , 23. ,  3. , 21. ,  3. ]])    '''    vwap_price = (pairs[0]*pairs[1]).sum()/pairs[1].sum()    try:assert pairs.shape[0] == 2    except: #$        breakpoint()    assert len(pairs.shape) == 2    return vwap_pricedef broadcast_lists(list1, list2):    shorter, longer = sorted([list1, list2], key=len)    difference = 1000 if shorter is list1 else -1000    for _ in range((len(longer) - len(shorter)) // 2):        try:            last_element = [shorter[-2] + difference, 0]        except:            price_fillna = Config.ask_fillna if shorter is list1 else Config.bid_fillna            last_element = [price_fillna, 0]        shorter.extend(last_element)    result = np.array([list1, list2])    return result